$.ajax("baby-steps.json").done(function(a){var e;for(i=1;i<8;i++){e=$.grep(a.friends,function(a,e){return a.babyStep===i});var s=$(".bs"+i+"-friends");switch(e.length){case 0:break;case 1:s.append("<a href='#'>"+e[0].firstName+" "+e[0].lastName+"</a>");s.append(" is also in Baby Step "+i).show();break;case 2:s.append("<a href='#'>"+e[0].firstName+" "+e[0].lastName+"</a>");s.append(" and <a href='#'>"+e[1].firstName+" "+e[1].lastName+"</a>");s.append(" are also in Baby Step "+i).show();break;case 3:s.append("<a href='#'>"+e[0].firstName+" "+e[0].lastName+"</a>");s.append(", <a href='#'>"+e[1].firstName+" "+e[1].lastName+"</a>");s.append(", and 1 other friend are also in Baby Step "+i).show();break;default:var r=e.length-2;s.append("<a href='#'>"+e[0].firstName+" "+e[0].lastName+"</a>");s.append(", <a href='#'>"+e[1].firstName+" "+e[1].lastName+"</a>");s.append(", and "+r+" other friends are also in Baby Step "+i).show()}}});